mapper.select:
		select qna_no, qna_type, qna_subject, qna_content, qna_answer
		from(
		select qna_no, qna_type, qna_subject, qna_content, qna_answer, rownum rnum
		from QNA
		where user_id = #{id}
		order by qna_no desc
		)
		where rnum between #{start_num} and #{end_num}

		select count(*) cnt
		from QNA
		where user_id = #{id}
